 CREATE TABLE "HR"."CUSTOMERS" 
   (	"CUSTOMER#" NUMBER(4,0), 
	"LASTNAME" VARCHAR2(10 BYTE) NOT NULL ENABLE, 
	"FIRSTNAME" VARCHAR2(10 BYTE) NOT NULL ENABLE, 
	"ADDRESS" VARCHAR2(20 BYTE), 
	"CITY" VARCHAR2(12 BYTE), 
	"STATE" VARCHAR2(2 BYTE), 
	"ZIP" VARCHAR2(5 BYTE), 
	"REFERRED" NUMBER(4,0), 
	"REGION" CHAR(2 BYTE)
   );
SET DEFINE OFF;

CREATE TABLE "HR"."BOOKS" 
   (	"ISBN" VARCHAR2(10 BYTE), 
	"TITLE" VARCHAR2(30 BYTE), 
	"PUBDATE" DATE, 
	"PUBID" NUMBER(2,0), 
	"COST" NUMBER(5,2), 
	"RETAIL" NUMBER(5,2), 
	"DISCOUNT" NUMBER(4,2), 
	"CATEGORY" VARCHAR2(12 BYTE)
   ) ;
SET DEFINE OFF;

  CREATE TABLE "HR"."PUBLISHER" 
   (	"PUBID" NUMBER(2,0), 
	"NAME" VARCHAR2(23 BYTE), 
	"CONTACT" VARCHAR2(15 BYTE), 
	"PHONE" VARCHAR2(12 BYTE)
   ); 
   
